INSERT INTO `flowable_process` (`id`, `process_key`, `process_name`, `process_xml`, `context`) VALUES (7, 'leaveApproval9', '返回请假提交申请', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<bpmn:definitions xmlns:bpmn=\"http://www.omg.org/spec/BPMN/20100524/MODEL\" xmlns:xsi=\"http://www.w3.org/2001/XMLSchema-instance\" xmlns:bpmndi=\"http://www.omg.org/spec/BPMN/20100524/DI\" xmlns:dc=\"http://www.omg.org/spec/DD/20100524/DC\" xmlns:di=\"http://www.omg.org/spec/DD/20100524/DI\" xmlns:flowable=\"http://flowable.org/bpmn\" targetNamespace=\"http://www.flowable.org/processdef\">\n    <bpmn:process id=\"leaveApproval9\" name=\"请假审批流程\" isExecutable=\"true\">\n        <!-- 流程变量定义 -->\n        <bpmn:extensionElements>\n            <flowable:variables>\n                <flowable:variable name=\"approvalResult\" type=\"string\"/>\n            </flowable:variables>\n        </bpmn:extensionElements>\n\n        <!-- 开始事件 -->\n        <bpmn:startEvent id=\"startEvent\" name=\"开始\"/>\n\n        <!-- 用户任务：提交申请 -->\n        <bpmn:userTask id=\"applyTask\" name=\"提交请假申请\" flowable:assignee=\"${employee}\"/>\n\n        <!-- 用户任务：经理审批 -->\n        <bpmn:userTask id=\"approveTask\" name=\"经理审批\" flowable:assignee=\"${manager}\">\n            <bpmn:extensionElements>\n                <!-- 任务完成监听器 - 捕获审批结果 -->\n                <flowable:taskListener event=\"complete\" class=\"top.fusuccess.flowabletutorial.tutorial05_reject.ApprovalResultListener\"/>\n            </bpmn:extensionElements>\n        </bpmn:userTask>\n\n        <!-- 决策网关 -->\n        <bpmn:exclusiveGateway id=\"decisionGateway\" name=\"审批结果判断\"/>\n\n        <!-- 结束事件 -->\n        <bpmn:endEvent id=\"endEvent\" name=\"结束\"/>\n\n        <!-- 拒绝路径（直接返回申请节点） -->\n        <bpmn:sequenceFlow id=\"rejectFlow\" sourceRef=\"decisionGateway\" targetRef=\"applyTask\">\n            <bpmn:conditionExpression xsi:type=\"bpmn:tFormalExpression\">\n                ${approvalResult == \'reject\'}\n            </bpmn:conditionExpression>\n        </bpmn:sequenceFlow>\n\n        <!-- 同意路径 -->\n        <bpmn:sequenceFlow id=\"approveFlow\" sourceRef=\"decisionGateway\" targetRef=\"endEvent\">\n            <bpmn:conditionExpression xsi:type=\"bpmn:tFormalExpression\">\n                ${approvalResult == \'approve\'}\n            </bpmn:conditionExpression>\n        </bpmn:sequenceFlow>\n\n        <!-- 原始序列流 -->\n        <bpmn:sequenceFlow id=\"flow1\" sourceRef=\"startEvent\" targetRef=\"applyTask\"/>\n        <bpmn:sequenceFlow id=\"flow2\" sourceRef=\"applyTask\" targetRef=\"approveTask\"/>\n        <bpmn:sequenceFlow id=\"flow3\" sourceRef=\"approveTask\" targetRef=\"decisionGateway\"/>\n    </bpmn:process>\n\n    <!-- 图形布局定义 -->\n    <bpmndi:BPMNDiagram id=\"BPMNDiagram_leaveApproval9\">\n        <bpmndi:BPMNPlane id=\"BPMNPlane_leaveApproval\" bpmnElement=\"leaveApproval9\">\n            <!-- 开始事件 -->\n            <bpmndi:BPMNShape id=\"startEvent_di\" bpmnElement=\"startEvent\">\n                <dc:Bounds x=\"152\" y=\"102\" width=\"36\" height=\"36\"/>\n            </bpmndi:BPMNShape>\n\n            <!-- 提交申请任务 -->\n            <bpmndi:BPMNShape id=\"applyTask_di\" bpmnElement=\"applyTask\">\n                <dc:Bounds x=\"240\" y=\"80\" width=\"100\" height=\"80\"/>\n            </bpmndi:BPMNShape>\n\n            <!-- 经理审批任务 -->\n            <bpmndi:BPMNShape id=\"approveTask_di\" bpmnElement=\"approveTask\">\n                <dc:Bounds x=\"400\" y=\"80\" width=\"100\" height=\"80\"/>\n            </bpmndi:BPMNShape>\n\n            <!-- 决策网关 -->\n            <bpmndi:BPMNShape id=\"decisionGateway_di\" bpmnElement=\"decisionGateway\">\n                <dc:Bounds x=\"560\" y=\"95\" width=\"50\" height=\"50\"/>\n            </bpmndi:BPMNShape>\n\n            <!-- 结束事件 -->\n            <bpmndi:BPMNShape id=\"endEvent_di\" bpmnElement=\"endEvent\">\n                <dc:Bounds x=\"670\" y=\"102\" width=\"36\" height=\"36\"/>\n            </bpmndi:BPMNShape>\n\n            <!-- 序列流定义（带箭头） -->\n            <bpmndi:BPMNEdge id=\"flow1_di\" bpmnElement=\"flow1\">\n                <di:waypoint x=\"188\" y=\"120\"/>\n                <di:waypoint x=\"240\" y=\"120\"/>\n            </bpmndi:BPMNEdge>\n\n            <bpmndi:BPMNEdge id=\"flow2_di\" bpmnElement=\"flow2\">\n                <di:waypoint x=\"340\" y=\"120\"/>\n                <di:waypoint x=\"400\" y=\"120\"/>\n            </bpmndi:BPMNEdge>\n\n            <bpmndi:BPMNEdge id=\"flow3_di\" bpmnElement=\"flow3\">\n                <di:waypoint x=\"500\" y=\"120\"/>\n                <di:waypoint x=\"560\" y=\"120\"/>\n            </bpmndi:BPMNEdge>\n\n            <bpmndi:BPMNEdge id=\"approveFlow_di\" bpmnElement=\"approveFlow\">\n                <di:waypoint x=\"610\" y=\"120\"/>\n                <di:waypoint x=\"670\" y=\"120\"/>\n            </bpmndi:BPMNEdge>\n\n            <bpmndi:BPMNEdge id=\"rejectFlow_di\" bpmnElement=\"rejectFlow\">\n                <di:waypoint x=\"585\" y=\"95\"/>  <!-- 网关顶部 -->\n                <di:waypoint x=\"585\" y=\"30\"/>  <!-- 向上 -->\n                <di:waypoint x=\"290\" y=\"30\"/>  <!-- 向左 -->\n                <di:waypoint x=\"290\" y=\"80\"/>  <!-- 向下到提交任务顶部 -->\n            </bpmndi:BPMNEdge>\n        </bpmndi:BPMNPlane>\n    </bpmndi:BPMNDiagram>\n</bpmn:definitions>\n', '');
INSERT INTO `flowable_process` (`id`, `process_key`, `process_name`, `process_xml`, `context`) VALUES (8, 'leaveApproval10', '接收消息触发流程启动', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<bpmn:definitions xmlns:bpmn=\"http://www.omg.org/spec/BPMN/20100524/MODEL\" xmlns:xsi=\"http://www.w3.org/2001/XMLSchema-instance\" xmlns:bpmndi=\"http://www.omg.org/spec/BPMN/20100524/DI\" xmlns:dc=\"http://www.omg.org/spec/DD/20100524/DC\" xmlns:di=\"http://www.omg.org/spec/DD/20100524/DI\" xmlns:flowable=\"http://flowable.org/bpmn\" targetNamespace=\"http://www.flowable.org/processdef\">\n  <bpmn:message id=\"startMessageTask\" name=\"启动消息\" />  <!-- 添加的消息定义 -->\n  <bpmn:process id=\"leaveApproval10\" name=\"请假审批流程_fromDb\" isExecutable=\"true\">\n    <startEvent xmlns=\"http://www.omg.org/spec/BPMN/20100524/MODEL\" id=\"sid-8e4484ec-97d5-46da-8a96-18664e42a998\" name=\"消息启动流程\">\n      <messageEventDefinition id=\"messageEventDefinition\" messageRef=\"startMessageTask\"/>\n    </startEvent>\n    <bpmn:userTask id=\"applyTask\" name=\"提交请假申请\" flowable:assignee=\"${employee}\"/>\n    <bpmn:userTask id=\"approveTask\" name=\"经理审批\" flowable:assignee=\"${manager}\"/>\n    <bpmn:endEvent id=\"endEvent\" name=\"结束\"/>\n    <serviceTask xmlns=\"http://www.omg.org/spec/BPMN/20100524/MODEL\" id=\"mailSend\" flowable:exclusive=\"false\" name=\"邮件发送\" flowable:class=\"top.fusuccess.flowabletutorial.tutorial08.ServicesTask\" flowable:skipExpression=\"${skipTask}\"></serviceTask>\n    <bpmn:sequenceFlow id=\"flow1\" sourceRef=\"sid-8e4484ec-97d5-46da-8a96-18664e42a998\" targetRef=\"applyTask\"/>\n    <bpmn:sequenceFlow id=\"flow2\" sourceRef=\"applyTask\" targetRef=\"mailSend\"/>\n    <bpmn:sequenceFlow id=\"flow3\" sourceRef=\"approveTask\" targetRef=\"endEvent\"/>\n    <sequenceFlow xmlns=\"http://www.omg.org/spec/BPMN/20100524/MODEL\" id=\"sid-d8bd7448-cb5d-4a5c-be57-241141524f70\" sourceRef=\"mailSend\" targetRef=\"approveTask\"></sequenceFlow>\n  </bpmn:process>\n  <bpmndi:BPMNDiagram id